import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import './Expenses.css';
import { useState } from "react";

function Expenses({expenses}) {

    const [filteredExpense, setFilteredExpense] = useState(expenses);

    const changeFilterHandler = (year) => {
        setFilteredExpense(filteredExpense.filter((expense) => {
            return expense.getFullYear() === year
        }))
    }

    return (
    <Card className="expenses">
        <ExpensesFilter onChangeFilter={changeFilterHandler}/>
        <ExpenseItem detail={expenses[0]}></ExpenseItem>
        <ExpenseItem detail={expenses[1]}></ExpenseItem>
        <ExpenseItem detail={expenses[2]}></ExpenseItem>
        <ExpenseItem detail={expenses[3]}></ExpenseItem>
    </Card>
);
}

export default Expenses;
