import './NewExpense.css'
import ExpenseForm from './ExpenseForm';
import { useState } from 'react/cjs/react.production.min';

const NewExpense = ({onAddExpenseData}) => {

    const [isFormExpanded, setIsFormExpanded] = useState(false);

    const saveExpenseDataHandler = (enteredExpenseData) => {
        const exponseData = {
            ...enteredExpenseData,
            id: Math.random.toString(),
        };

        onAddExpenseData(exponseData);

        setIsFormExpanded(false);
    }

    const expandFormHandler = () => {
        setIsFormExpanded(true);
    }

    let expenseForm = <NewExpenseButton onExpandForm={expandFormHandler}/>

    if (isFormExpanded) {
        expenseForm = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
    }

    return ( 
        <div className="new-expense">
            {
                expenseForm
            }
        </div>
    )
}

export default NewExpense;
